import React from 'react';
import Card from '../UI/Card/Card';
import './LogsForm.css';
// import './LogsForm.css';

const LogsForm = () => {

    let inputDate = '';
    let inputDesc = '';
    let inputTime = 0;

    const  dateChangeHandler = (event) => {
        inputDate = event.target.value;
    };

    const descChangeHandler = (event) => {
        inputDesc = event.target.value;
    };

    const timeChangeHandler = (event) => {      
        inputTime = event.target.value;
    };

    const formSubmitHandler = (event) => {
        event.preventDefault();
    }

    const newLog ={
        date: new Date(inputDate),
        desc: inputDesc,
        time: +inputTime,

    }

        console.log(newLog);


  return (
    <Card className="logs-form">
        <form>
            <div className='form-item'>
                <label htmlFor='date'>日期</label>
                <input onChange={dateChangeHandler} id="date" type="date" />
            </div>

            <div className='form-item'>
                <label htmlFor='desc'>内容</label>
                <input onChange={descChangeHandler} id="desc" type="text" />
            </div>

            <div className='form-item'>
                <label htmlFor='time'>时长</label>
                <input onChange={timeChangeHandler} id="time" type="number" />
            </div>

            <div className="form-btn">
                <button onChange={formSubmitHandler}>添加</button>
            </div>
        </form>
    </Card>
  )
}

export default LogsForm
